import { createRouter,createWebHashHistory } from "vue-router";



//配置信息中需要页面的相关配置
const routes = [
  {path: '/Login', name: 'Login', component: () => import("@/views/LoginViewHome")},
  {
    path:"/login",
    name:'login',
    component: () => import("../views/RegistViewsHome.vue")
  },
]


const router = createRouter({

      history:createWebHashHistory(),
      routes
})
export default router;
